def separate_words(input_list):
    ends_with_ष = []
    ends_with_इर = []

    for word in input_list:
        print(f"Checking word: {word}")
        if word.endswith('ष्'):
            ends_with_ष.append(word)
        elif word.endswith('इर्'):
            ends_with_इर.append(word)

    return ends_with_ष, ends_with_इर


def separate_words_by_prefix(input_list):
    starts_with_टु = []
    starts_with_डु = []
    starts_with_ञि = []

    for word in input_list:
        print(f"Checking word: {word}")
        if word.startswith('टु'):
            starts_with_टु.append(word)
        elif word.startswith('डु'):
            starts_with_डु.append(word)
        elif word.startswith('ञि'):
            starts_with_ञि.append(word)

    return starts_with_टु, starts_with_डु, starts_with_ञि


