def separate_words_by_special_chars(input_list, special_chars):
    # Dictionary to hold words for each special character
    char_to_words = {char: [] for char in special_chars}

    # Iterate through the input list and classify words
    for word in input_list:
        for char in special_chars:
            if char in word:
                char_to_words[char].append(word)

    # Return the dictionary
    return char_to_words


